def search_xmas(matrix, start_row, start_col, inc_row, inc_col)
    if inc_row == 0 and inc_col == 0
        return false
    end
    if start_row + 3 * inc_row >= matrix.length or start_col + 3 * inc_col >= matrix[start_row].length
        return false
    end
    if start_row + 3 * inc_row < 0 or start_col + 3 * inc_col < 0
        return false
    end
    return (
        matrix[start_row + 1 * inc_row][start_col + 1 * inc_col] == 'M' and
        matrix[start_row + 2 * inc_row][start_col + 2 * inc_col] == 'A' and
        matrix[start_row + 3 * inc_row][start_col + 3 * inc_col] == 'S'
    )
end

def search_x_mas(matrix, start_row, start_col)
    if start_row - 1 < 0 or start_col - 1 < 0 or start_row + 1 >= matrix.length or start_col + 1 >= matrix[start_row].length
        return false
    end
    return (
        ((matrix[start_row - 1][start_col - 1] == 'M' and matrix[start_row + 1][start_col + 1] == 'S') or
        (matrix[start_row - 1][start_col - 1] == 'S' and matrix[start_row + 1][start_col + 1] == 'M')) and
        ((matrix[start_row - 1][start_col + 1] == 'M' and matrix[start_row + 1][start_col - 1] == 'S') or
        (matrix[start_row - 1][start_col + 1] == 'S' and matrix[start_row + 1][start_col - 1] == 'M'))
    )
end

xmas_count = 0
x_mas_count = 0
matrix = PUZZLE.split("\n")
for row in 0...matrix.length
    for col in 0...matrix[row].length
        # xmas
        if matrix[row][col] == 'X'
            for i in -1..1
                for j in -1..1
                    if search_xmas(matrix, row, col, i, j)
                        xmas_count += 1
                    end
                end
            end
        end
        # x-mas
        if matrix[row][col] == 'A'
            if search_x_mas(matrix, row, col)
                x_mas_count += 1
            end
        end
    end
end

puts("xmas_count: #{xmas_count}")
puts("x_mas_count: #{x_mas_count}")